// eBPF program for ZK71 Zone 0
#include <linux/bpf.h>
#include <linux/ptrace.h>

BPF_HASH(zone_0_stats, u32, u64);

int trace_zone_0(struct pt_regs *ctx) {
    u32 pid = bpf_get_current_pid_tgid() >> 32;
    u64 *count = zone_0_stats.lookup(&pid);

    if (count) {
        (*count)++;
    } else {
        u64 init = 1;
        zone_0_stats.update(&pid, &init);
    }

    // Rate limit by prime 2
    if (*count % 2 == 0) {
        bpf_trace_printk("Zone 0: PID %d hit prime boundary\n", pid);
    }

    return 0;
}

